import React, { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';
import '../styles/pawsbot-chat.css';

const PawsBot = () => {
  const { user } = useAuth();
  const { isDarkMode } = useTheme();
  const navigate = useNavigate();
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  // Predefined questions for quick access
  const quickQuestions = [
    {
      icon: '🍽️',
      title: 'Pet Nutrition & Feeding',
      question: 'What is the best diet for my dog? How often should I feed them?'
    },
    {
      icon: '🏥',
      title: 'Health & Wellness',
      question: 'What are the common signs of illness in pets that I should watch for?'
    },
    {
      icon: '🎓',
      title: 'Training & Behavior',
      question: 'How can I train my puppy to stop biting and follow basic commands?'
    },
    {
      icon: '🐕',
      title: 'Dog Care Tips',
      question: 'What are the essential grooming needs for dogs?'
    },
    {
      icon: '🐱',
      title: 'Cat Care Tips',
      question: 'Why is my cat not using the litter box and how can I fix it?'
    },
    {
      icon: '💉',
      title: 'Vaccinations & Vet Visits',
      question: 'What vaccinations does my pet need and how often should I visit the vet?'
    }
  ];

  useEffect(() => {
    // Check if user is logged in
    if (!user) {
      navigate('/login', { state: { from: '/pawsbot', message: 'Please log in to use PawsBot' } });
      return;
    }

    // Initial welcome message
    setMessages([{
      id: Date.now(),
      type: 'bot',
      content: `Hi ${user.username}! 👋 I'm PawsBot, your AI pet care assistant. Ask me anything about your furry friends!`,
      timestamp: new Date()
    }]);

    // Check if Botpress is already loaded
    if (window.botpress) {
      console.log('Botpress already loaded');
      return;
    }

    // Load Botpress v3.3 webchat script
    const script = document.createElement('script');
    script.src = 'https://cdn.botpress.cloud/webchat/v3.3/inject.js';
    script.async = true;
    
    script.onload = () => {
      console.log('Botpress v3.3 script loaded');
      
      // Wait for botpress to be available
      const initBot = () => {
        if (window.botpress && window.botpress.init) {
          console.log('Initializing Botpress...');
          
          // Initialize Botpress with your configuration
          window.botpress.init({
            "botId": "a3eb89a4-cd2e-4e91-b65c-e6fe34dffe9b",
            "configuration": {
              "version": "v2",
              "composerPlaceholder": "Ask about your furry friends",
              "botName": "Pawsbot",
              "website": {},
              "email": {},
              "phone": {},
              "termsOfService": {},
              "privacyPolicy": {},
              "color": "#8B5CF6",
              "variant": "solid",
              "headerVariant": "glass",
              "themeMode": isDarkMode ? "dark" : "light",
              "fontFamily": "inter",
              "radius": 4,
              "feedbackEnabled": false,
              "footer": "",
              "soundEnabled": false,
              "embeddedChatId": "bp-embedded-webchat"
            },
            "clientId": "f540c4a7-c98e-44f5-bc83-752633f03bbf"
          });
          
          console.log('Botpress initialized successfully!');
          
          // Auto-open the chat when ready
          window.botpress.on("webchat:ready", () => {
            console.log('Botpress webchat ready!');
            window.botpress.open();
          });
          
        } else {
          console.log('Waiting for window.botpress...');
          setTimeout(initBot, 100);
        }
      };
      
      setTimeout(initBot, 100);
    };

    script.onerror = () => {
      console.error('Failed to load Botpress script');
    };

    document.body.appendChild(script);

    return () => {
      // Cleanup scripts on unmount
      const scripts = document.querySelectorAll('script[src*="botpress"]');
      scripts.forEach(s => {
        if (document.body.contains(s)) {
          document.body.removeChild(s);
        }
      });
    };
  }, [user, navigate]);

  // Scroll to bottom when messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Handle quick question click
  const handleQuickQuestion = (question) => {
    setInputMessage(question);
    inputRef.current?.focus();
  };

  // Send message to Botpress
  const sendMessage = async (e) => {
    e?.preventDefault();
    if (!inputMessage.trim() || isLoading) return;

    const userMessage = {
      id: Date.now(),
      type: 'user',
      content: inputMessage,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    const currentMessage = inputMessage;
    setInputMessage('');
    setIsLoading(true);

    try {
      // Send message to Botpress v3.3
      if (window.botpress && window.botpress.sendEvent) {
        console.log('Sending message to Botpress:', currentMessage);
        
        // Send the message using v3.3 API
        window.botpress.sendEvent({
          type: 'text',
          payload: { text: currentMessage }
        });

        // Show a response that the message was sent
        setTimeout(() => {
          const botMessage = {
            id: Date.now() + 1,
            type: 'bot',
            content: 'I received your question! You can see my response in the chat widget below. 💬',
            timestamp: new Date()
          };
          setMessages(prev => [...prev, botMessage]);
          setIsLoading(false);
        }, 500);
        
      } else {
        // Botpress not available yet
        console.warn('Botpress not available');
        const botMessage = {
          id: Date.now() + 1,
          type: 'bot',
          content: 'The chat widget is loading... Please wait a moment and try again, or use the widget below when it appears! 🤖',
          timestamp: new Date()
        };
        setMessages(prev => [...prev, botMessage]);
        setIsLoading(false);
      }
    } catch (error) {
      console.error('Error in sendMessage:', error);
      const errorMessage = {
        id: Date.now() + 1,
        type: 'bot',
        content: 'Please use the Botpress chat widget below to chat with me!',
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorMessage]);
      setIsLoading(false);
    }
  };

  // Handle Enter key
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div className="pawsbot-chat-container">
      {/* Header */}
      <div className="pawsbot-chat-header">
        <div className="header-content">
          <div className="header-left">
            <div className="bot-avatar">
              <span className="bot-icon">🤖</span>
            </div>
            <div className="header-info">
              <h1 className="bot-title">PawsBot AI Assistant</h1>
              <p className="bot-subtitle">Your AI companion for pet care advice</p>
            </div>
          </div>
        </div>
      </div>

      {/* Main Chat Area */}
      <div className="pawsbot-chat-main">
        {messages.length === 1 ? (
          /* Welcome Screen with Quick Questions */
          <div className="welcome-screen">
            <div className="welcome-content">
              <div className="welcome-icon">
                <span>🐾</span>
              </div>
              <h2 className="welcome-title">Welcome to PawsCare AI!</h2>
              <p className="welcome-text">
                Ask me anything about your pet's health, behavior, training, or general care.
              </p>
              
              <div className="quick-questions-grid">
                <p className="quick-questions-label">Try asking me about:</p>
                {quickQuestions.map((item, index) => (
                  <button
                    key={index}
                    className="quick-question-card"
                    onClick={() => handleQuickQuestion(item.question)}
                  >
                    <span className="quick-question-icon">{item.icon}</span>
                    <span className="quick-question-title">{item.title}</span>
                    <span className="quick-question-preview">{item.question}</span>
                  </button>
                ))}
              </div>
            </div>
          </div>
        ) : (
          /* Chat Messages */
          <div className="chat-messages">
            {messages.map((message) => (
              <div
                key={message.id}
                className={`message-wrapper ${message.type === 'user' ? 'user-message' : 'bot-message'}`}
              >
                {message.type === 'bot' && (
                  <div className="message-avatar">
                    <span className="avatar-icon">🤖</span>
                  </div>
                )}
                <div className="message-content">
                  <div className="message-bubble">
                    <p className="message-text">{message.content}</p>
                  </div>
                  <span className="message-time">
                    {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                  </span>
                </div>
                {message.type === 'user' && (
                  <div className="message-avatar user-avatar">
                    <span className="avatar-icon">👤</span>
                  </div>
                )}
              </div>
            ))}
            
            {isLoading && (
              <div className="message-wrapper bot-message">
                <div className="message-avatar">
                  <span className="avatar-icon">🤖</span>
                </div>
                <div className="message-content">
                  <div className="message-bubble typing-indicator">
                    <span></span>
                    <span></span>
                    <span></span>
                  </div>
                </div>
              </div>
            )}
            
            <div ref={messagesEndRef} />
          </div>
        )}
      </div>

      {/* Input Area */}
      <div className="pawsbot-chat-input">
        <form onSubmit={sendMessage} className="input-form">
          <div className="input-wrapper">
            <textarea
              ref={inputRef}
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Ask anything about your pet..."
              className="chat-input"
              rows="1"
              disabled={isLoading}
            />
            <button
              type="submit"
              className="send-button"
              disabled={!inputMessage.trim() || isLoading}
            >
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <line x1="22" y1="2" x2="11" y2="13"></line>
                <polygon points="22 2 15 22 11 13 2 9 22 2"></polygon>
              </svg>
            </button>
          </div>
        </form>
        <p className="input-disclaimer">
          PawsBot can make mistakes. Always consult with a veterinarian for serious health concerns.
        </p>
      </div>
    </div>
  );
};

export default PawsBot;
