import React, { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';

const PawsBot = () => {
  const { user } = useAuth();
  const { isDarkMode } = useTheme();
  const navigate = useNavigate();
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  // Predefined questions for quick access
  const quickQuestions = [
    {
      icon: '🍽️',
      title: 'Pet Nutrition & Feeding',
      question: 'What is the best diet for my dog? How often should I feed them?'
    },
    {
      icon: '🏥',
      title: 'Health & Wellness',
      question: 'What are the common signs of illness in pets that I should watch for?'
    },
    {
      icon: '🎓',
      title: 'Training & Behavior',
      question: 'How can I train my puppy to stop biting and follow basic commands?'
    },
    {
      icon: '🐕',
      title: 'Dog Care Tips',
      question: 'What are the essential grooming needs for dogs?'
    },
    {
      icon: '🐱',
      title: 'Cat Care Tips',
      question: 'Why is my cat not using the litter box and how can I fix it?'
    },
    {
      icon: '💉',
      title: 'Vaccinations & Vet Visits',
      question: 'What vaccinations does my pet need and how often should I visit the vet?'
    }
  ];

  useEffect(() => {
    // Check if user is logged in
    if (!user) {
      navigate('/login', { state: { from: '/pawsbot', message: 'Please log in to use PawsBot' } });
      return;
    }

    // Initial welcome message
    setMessages([{
      id: Date.now(),
      type: 'bot',
      content: `Hi ${user.username}! 👋 I'm PawsBot, your AI pet care assistant. Ask me anything about your furry friends!`,
      timestamp: new Date()
    }]);

    // Check if Botpress is already loaded
    if (window.botpressWebChat) {
      console.log('Botpress already loaded');
      const bpWidget = document.getElementById('bp-web-widget-container');
      if (bpWidget) {
        bpWidget.style.display = 'none';
      }
      return;
    }

    // Load Botpress scripts
    const script1 = document.createElement('script');
    script1.src = 'https://cdn.botpress.cloud/webchat/v3.3/inject.js';
    script1.async = true;

    const script2 = document.createElement('script');
    script2.src = 'https://files.bpcontent.cloud/2025/10/01/08/20251001082958-80TU2NR9.js';
    script2.defer = true;

    let botpressReady = false;

    script1.onload = () => {
      console.log('Botpress inject.js loaded');
    };

    script2.onload = () => {
      console.log('Botpress config loaded');
      botpressReady = true;

      // Wait for Botpress to initialize
      const checkBotpress = setInterval(() => {
        if (window.botpressWebChat) {
          console.log('Botpress webchat is ready!');
          clearInterval(checkBotpress);

          // Hide the default Botpress widget
          setTimeout(() => {
            const bpWidget = document.getElementById('bp-web-widget-container');
            if (bpWidget) {
              bpWidget.style.display = 'none';
              console.log('Botpress widget hidden');
            }
          }, 1000);
        }
      }, 500);

      // Stop checking after 10 seconds
      setTimeout(() => {
        clearInterval(checkBotpress);
        if (!window.botpressWebChat) {
          console.error('Botpress failed to initialize');
        }
      }, 10000);
    };

    script1.onerror = () => {
      console.error('Failed to load Botpress inject.js');
    };

    script2.onerror = () => {
      console.error('Failed to load Botpress config');
    };

    document.body.appendChild(script1);
    document.body.appendChild(script2);

    return () => {
      if (document.body.contains(script1)) document.body.removeChild(script1);
      if (document.body.contains(script2)) document.body.removeChild(script2);
    };
  }, [user, navigate]);

  // Scroll to bottom when messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Handle quick question click
  const handleQuickQuestion = (question) => {
    setInputMessage(question);
    inputRef.current?.focus();
  };

  // Send message to Botpress
  const sendMessage = async (e) => {
    e?.preventDefault();
    if (!inputMessage.trim() || isLoading) return;

    const userMessage = {
      id: Date.now(),
      type: 'user',
      content: inputMessage,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    const currentMessage = inputMessage;
    setInputMessage('');
    setIsLoading(true);

    try {
      // Send to Botpress webchat and wait for response
      if (window.botpressWebChat) {
        // Open the Botpress widget temporarily to send message
        const bpWidget = document.getElementById('bp-web-widget-container');
        if (bpWidget) {
          bpWidget.style.display = 'block';
          bpWidget.style.opacity = '0';
          bpWidget.style.pointerEvents = 'none';
        }

        // Send message to Botpress
        window.botpressWebChat.sendEvent({
          type: 'proactive-trigger',
          channel: 'web',
          payload: { text: currentMessage }
        });

        // Listen for Botpress response
        let responseReceived = false;
        const checkForResponse = setInterval(() => {
          // Try to get the last message from Botpress
          const botpressMessages = document.querySelectorAll('.bpw-from-bot .bpw-chat-bubble-content');
          if (botpressMessages.length > 0) {
            const lastBotMessage = botpressMessages[botpressMessages.length - 1];
            if (lastBotMessage && lastBotMessage.textContent) {
              responseReceived = true;
              clearInterval(checkForResponse);

              const botMessage = {
                id: Date.now() + 1,
                type: 'bot',
                content: lastBotMessage.textContent,
                timestamp: new Date()
              };
              setMessages(prev => [...prev, botMessage]);
              setIsLoading(false);

              // Hide Botpress widget again
              if (bpWidget) {
                bpWidget.style.display = 'none';
              }
            }
          }
        }, 500);

        // Timeout after 10 seconds
        setTimeout(() => {
          if (!responseReceived) {
            clearInterval(checkForResponse);
            const botMessage = {
              id: Date.now() + 1,
              type: 'bot',
              content: 'I apologize, but I\'m having trouble responding right now. Please try again in a moment.',
              timestamp: new Date()
            };
            setMessages(prev => [...prev, botMessage]);
            setIsLoading(false);

            if (bpWidget) {
              bpWidget.style.display = 'none';
            }
          }
        }, 10000);
      } else {
        // Botpress not loaded yet
        const botMessage = {
          id: Date.now() + 1,
          type: 'bot',
          content: 'PawsBot is still loading. Please wait a moment and try again.',
          timestamp: new Date()
        };
        setMessages(prev => [...prev, botMessage]);
        setIsLoading(false);
      }
    } catch (error) {
      console.error('Error sending message:', error);
      const errorMessage = {
        id: Date.now() + 1,
        type: 'bot',
        content: 'Sorry, I encountered an error. Please try again.',
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorMessage]);
      setIsLoading(false);
    }
  };

  // Handle Enter key
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div className="pawsbot-chat-container">
      {/* Header */}
      <div className="pawsbot-chat-header">
        <div className="header-content">
          <div className="header-left">
            <div className="bot-avatar">
              <span className="bot-icon">🤖</span>
            </div>
            <div className="header-info">
              <h1 className="bot-title">PawsBot AI Assistant</h1>
              <p className="bot-subtitle">Your AI companion for pet care advice</p>
            </div>
          </div>
        </div>
      </div>

      {/* Main Chat Area */}
      <div className="pawsbot-chat-main">
        {messages.length === 1 ? (
          /* Welcome Screen with Quick Questions */
          <div className="welcome-screen">
            <div className="welcome-content">
              <div className="welcome-icon">
                <span>🐾</span>
              </div>
              <h2 className="welcome-title">Welcome to PawsCare AI!</h2>
              <p className="welcome-text">
                Ask me anything about your pet's health, behavior, training, or general care.
              </p>

              <div className="quick-questions-grid">
                <p className="quick-questions-label">Try asking me about:</p>
                {quickQuestions.map((item, index) => (
                  <button
                    key={index}
                    className="quick-question-card"
                    onClick={() => handleQuickQuestion(item.question)}
                  >
                    <span className="quick-question-icon">{item.icon}</span>
                    <span className="quick-question-title">{item.title}</span>
                    <span className="quick-question-preview">{item.question}</span>
                  </button>
                ))}
              </div>
            </div>
          </div>
        ) : (
          /* Chat Messages */
          <div className="chat-messages">
            {messages.map((message) => (
              <div
                key={message.id}
                className={`message-wrapper ${message.type === 'user' ? 'user-message' : 'bot-message'}`}
              >
                {message.type === 'bot' && (
                  <div className="message-avatar">
                    <span className="avatar-icon">🤖</span>
                  </div>
                )}
                <div className="message-content">
                  <div className="message-bubble">
                    <p className="message-text">{message.content}</p>
                  </div>
                  <span className="message-time">
                    {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                  </span>
                </div>
                {message.type === 'user' && (
                  <div className="message-avatar user-avatar">
                    <span className="avatar-icon">👤</span>
                  </div>
                )}
              </div>
            ))}

            {isLoading && (
              <div className="message-wrapper bot-message">
                <div className="message-avatar">
                  <span className="avatar-icon">🤖</span>
                </div>
                <div className="message-content">
                  <div className="message-bubble typing-indicator">
                    <span></span>
                    <span></span>
                    <span></span>
                  </div>
                </div>
              </div>
            )}

            <div ref={messagesEndRef} />
          </div>
        )}
      </div>

      {/* Input Area */}
      <div className="pawsbot-chat-input">
        <form onSubmit={sendMessage} className="input-form">
          <div className="input-wrapper">
            <textarea
              ref={inputRef}
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Ask anything about your pet..."
              className="chat-input"
              rows="1"
              disabled={isLoading}
            />
            <button
              type="submit"
              className="send-button"
              disabled={!inputMessage.trim() || isLoading}
            >
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <line x1="22" y1="2" x2="11" y2="13"></line>
                <polygon points="22 2 15 22 11 13 2 9 22 2"></polygon>
              </svg>
            </button>
          </div>
