import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { QueryClient, QueryClientProvider } from 'react-query';
import { Toaster } from 'react-hot-toast';

// Contexts
import { AuthProvider } from './contexts/AuthContext';
import { ThemeProvider } from './contexts/ThemeContext';

// Pages
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import Categories from './pages/Categories';
import CategoryPosts from './pages/CategoryPosts';
import CategoryTimeline from './pages/CategoryTimeline';
import PostDetail from './pages/PostDetail';
import Profile from './pages/Profile';
import CreatePost from './pages/CreatePost';
import AuthCallback from './pages/AuthCallback';

// Components
import Navbar from './components/Navbar';
import ProtectedRoute from './components/ProtectedRoute';

// Styles
import './App.css';
import './styles/enhanced-theme.css';
import './styles/responsive-enhanced.css';
import './styles/modern.css';
import './styles/timeline.css';
import './styles/pet-avatar.css';
import './styles/cookie-consent.css';
import './styles/responsive.css';
import './styles/dark-mode.css';
import './styles/animations.css';
import './styles/modern-responsive.css';
import './styles/botpress-custom.css';

// Create a client for React Query
const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      retry: 1,
      staleTime: 5 * 60 * 1000, // 5 minutes
    },
  },
});

function App() {
  useEffect(() => {
    // Hide loading screen when React loads
    document.body.classList.add('loaded');

    // Load Botpress webchat bubble with proper sequencing
    const script1 = document.createElement('script');
    script1.src = 'https://cdn.botpress.cloud/webchat/v3.3/inject.js';
    script1.async = true;

    script1.onload = () => {
      console.log('✅ Botpress inject loaded');

      // Only load config after inject is ready
      const script2 = document.createElement('script');
      script2.src = 'https://files.bpcontent.cloud/2025/10/01/08/20251001082958-80TU2NR9.js';

      script2.onload = () => {
        console.log('✅ Botpress config loaded');

        // Wait for Botpress to fully initialize
        setTimeout(() => {
          // Make Botpress globally accessible for the card click
          window.openBotpressChat = () => {
            console.log('🤖 Opening PawsBot chat...');

            // Log all buttons for debugging
            const allButtons = document.querySelectorAll('button');
            console.log('All buttons:', allButtons);
            allButtons.forEach((btn, i) => {
              console.log(`Button ${i}:`, {
                id: btn.id,
                className: btn.className,
                ariaLabel: btn.getAttribute('aria-label'),
                innerHTML: btn.innerHTML.substring(0, 50)
              });
            });

            // Try to find the Botpress button - check all possible selectors
            let chatButton = 
              // Botpress v3 selectors
              document.querySelector('[data-testid="webchat-toggle-button"]') || 
              document.querySelector('.bpw-floating-button') ||
              document.querySelector('#bp-web-widget-container button') ||
              document.querySelector('button[aria-label*="chat" i]') ||
              // Generic webchat selectors
              document.querySelector('[class*="webchat"]') ||
              document.querySelector('[class*="botpress"]') ||
              document.querySelector('[id*="webchat"]') ||
              // Find by content
              Array.from(allButtons).find(btn => {
                const text = btn.textContent.toLowerCase();
                const ariaLabel = (btn.getAttribute('aria-label') || '').toLowerCase();
                const className = btn.className.toLowerCase();
                return text.includes('chat') || 
                       ariaLabel.includes('chat') || 
                       className.includes('webchat') ||
                       className.includes('botpress') ||
                       className.includes('bp-');
              });

            console.log('Found button:', chatButton);

            if (chatButton) {
              chatButton.click();
              console.log('✅ Chat opened successfully!');
            } else {
              console.log('⚠️ Button not found, trying API...');
              if (window.botpressWebChat) {
                try {
                  window.botpressWebChat.sendEvent({ type: 'show' });
                  console.log('✅ Chat opened via API');
                } catch (e) {
                  console.error('❌ Failed to open chat:', e);
                }
              } else {
                console.error('❌ window.botpressWebChat not available');
              }
            }
          };

          console.log('✅ PawsBot ready to chat!');
        }, 2000);
      };

      script2.onerror = () => {
        console.error('❌ Failed to load Botpress config');
      };

      document.body.appendChild(script2);
    };

    script1.onerror = () => {
      console.error('❌ Failed to load Botpress inject');
    };

    document.body.appendChild(script1);

    return () => {
      // Cleanup on unmount
      const scripts = document.querySelectorAll('script[src*="botpress"]');
      scripts.forEach(script => {
        if (document.body.contains(script)) {
          document.body.removeChild(script);
        }
      });
      delete window.openBotpressChat;
    };
  }, []);

  return (
    <QueryClientProvider client={queryClient}>
      <ThemeProvider>
        <AuthProvider>
          <Router>
            <div className="App">
              <Navbar />
              <main className="main-content">
                <Routes>
                  {/* Public routes */}
                  <Route path="/" element={<Home />} />
                  <Route path="/login" element={<Login />} />
                  <Route path="/register" element={<Register />} />
                  <Route path="/categories" element={<Categories />} />
                  <Route path="/category/:categoryId" element={<CategoryPosts />} />
                  <Route path="/timeline/:category" element={<CategoryTimeline />} />
                  <Route path="/post/:postId" element={<PostDetail />} />
                  <Route path="/profile/:username" element={<Profile />} />
                  <Route path="/auth/callback" element={<AuthCallback />} />

                  {/* Protected routes */}
                  <Route path="/create-post" element={
                    <ProtectedRoute>
                      <CreatePost />
                    </ProtectedRoute>
                  } />

                  {/* Redirect unknown routes to home */}
                  <Route path="*" element={<Navigate to="/" replace />} />
                </Routes>
              </main>

              {/* Toast notifications */}
              <Toaster
                position="top-right"
                toastOptions={{
                  duration: 4000,
                  style: {
                    background: 'var(--card-bg)',
                    color: 'var(--text-primary)',
                    border: '1px solid var(--border-color)',
                  },
                  success: {
                    iconTheme: {
                      primary: '#10B981',
                      secondary: '#FFFFFF',
                    },
                  },
                  error: {
                    iconTheme: {
                      primary: '#EF4444',
                      secondary: '#FFFFFF',
                    },
                  },
                }}
              />
            </div>
          </Router>
        </AuthProvider>
      </ThemeProvider>
    </QueryClientProvider>
  );
}

export default App;
