import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { QueryClient, QueryClientProvider } from 'react-query';
import { Toaster } from 'react-hot-toast';

// Contexts
import { AuthProvider } from './contexts/AuthContext';
import { ThemeProvider } from './contexts/ThemeContext';

// Pages
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import Categories from './pages/Categories';
import CategoryPosts from './pages/CategoryPosts';
import CategoryTimeline from './pages/CategoryTimeline';
import PostDetail from './pages/PostDetail';
import Profile from './pages/Profile';
import CreatePost from './pages/CreatePost';
import AuthCallback from './pages/AuthCallback';

// Components
import Navbar from './components/Navbar';
import ProtectedRoute from './components/ProtectedRoute';

// Styles
import './App.css';
import './styles/enhanced-theme.css';
import './styles/responsive-enhanced.css';
import './styles/modern.css';
import './styles/timeline.css';
import './styles/pet-avatar.css';
import './styles/cookie-consent.css';
import './styles/responsive.css';
import './styles/dark-mode.css';
import './styles/animations.css';
import './styles/modern-responsive.css';
import './styles/botpress-custom.css';

// Create a client for React Query
const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      retry: 1,
      staleTime: 5 * 60 * 1000, // 5 minutes
    },
  },
});

function App() {
  useEffect(() => {
    // Hide loading screen when React loads
    document.body.classList.add('loaded');

    // Load Botpress webchat bubble
    const script1 = document.createElement('script');
    script1.src = 'https://cdn.botpress.cloud/webchat/v3.3/inject.js';
    script1.async = true;

    const script2 = document.createElement('script');
    script2.src = 'https://files.bpcontent.cloud/2025/10/01/08/20251001082958-80TU2NR9.js';
    script2.defer = true;

    script1.onload = () => {
      console.log('Botpress inject loaded');
    };

    script2.onload = () => {
      console.log('Botpress config loaded');

      // Wait for Botpress to fully initialize
      setTimeout(() => {
        console.log('Checking for Botpress elements...');
        const allButtons = document.querySelectorAll('button');
        console.log('Total buttons found:', allButtons.length);

        // Make Botpress globally accessible for the card click
        window.openBotpressChat = () => {
          console.log('openBotpressChat called');

          // Try to find the Botpress button
          const chatButton = document.querySelector('[data-testid="webchat-toggle-button"]') || 
                            document.querySelector('.bpw-floating-button') ||
                            document.querySelector('#bp-web-widget-container button') ||
                            Array.from(document.querySelectorAll('button')).find(btn => 
                              btn.getAttribute('aria-label')?.includes('chat') ||
                              btn.className?.includes('webchat') ||
                              btn.id?.includes('webchat')
                            );

          console.log('Found chat button:', chatButton);

          if (chatButton) {
            chatButton.click();
            console.log('✅ Clicked chat button');
          } else {
            console.log('❌ Button not found, trying window methods...');
            console.log('window.botpressWebChat:', window.botpressWebChat);

            if (window.botpressWebChat) {
              window.botpressWebChat.sendEvent({ type: 'show' });
            }
          }
        };

        console.log('✅ window.openBotpressChat is ready');
      }, 1500);
    };

    document.body.appendChild(script1);
    document.body.appendChild(script2);

    return () => {
      // Cleanup on unmount
      if (document.body.contains(script1)) document.body.removeChild(script1);
      if (document.body.contains(script2)) document.body.removeChild(script2);
      delete window.openBotpressChat;
    };
  }, []);

  return (
    <QueryClientProvider client={queryClient}>
      <ThemeProvider>
        <AuthProvider>
          <Router>
            <div className="App">
              <Navbar />
              <main className="main-content">
                <Routes>
                  {/* Public routes */}
                  <Route path="/" element={<Home />} />
                  <Route path="/login" element={<Login />} />
                  <Route path="/register" element={<Register />} />
                  <Route path="/categories" element={<Categories />} />
                  <Route path="/category/:categoryId" element={<CategoryPosts />} />
                  <Route path="/timeline/:category" element={<CategoryTimeline />} />
                  <Route path="/post/:postId" element={<PostDetail />} />
                  <Route path="/profile/:username" element={<Profile />} />
                  <Route path="/auth/callback" element={<AuthCallback />} />

                  {/* Protected routes */}
                  <Route path="/create-post" element={
                    <ProtectedRoute>
                      <CreatePost />
                    </ProtectedRoute>
                  } />

                  {/* Redirect unknown routes to home */}
                  <Route path="*" element={<Navigate to="/" replace />} />
                </Routes>
              </main>

              {/* Toast notifications */}
              <Toaster
                position="top-right"
                toastOptions={{
                  duration: 4000,
                  style: {
                    background: 'var(--card-bg)',
                    color: 'var(--text-primary)',
                    border: '1px solid var(--border-color)',
                  },
                  success: {
                    iconTheme: {
                      primary: '#10B981',
                      secondary: '#FFFFFF',
                    },
                  },
                  error: {
                    iconTheme: {
                      primary: '#EF4444',
                      secondary: '#FFFFFF',
                    },
                  },
                }}
              />
            </div>
          </Router>
        </AuthProvider>
      </ThemeProvider>
    </QueryClientProvider>
  );
}

export default App;
