import React, { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';
import '../styles/pawsbot-chat.css';

const PawsBot = () => {
  const { user } = useAuth();
  const { isDarkMode } = useTheme();
  const navigate = useNavigate();
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  // Predefined questions for quick access
  const quickQuestions = [
    {
      icon: 'üçΩÔ∏è',
      title: 'Pet Nutrition & Feeding',
      question: 'What is the best diet for my dog? How often should I feed them?'
    },
    {
      icon: 'üè•',
      title: 'Health & Wellness',
      question: 'What are the common signs of illness in pets that I should watch for?'
    },
    {
      icon: 'üéì',
      title: 'Training & Behavior',
      question: 'How can I train my puppy to stop biting and follow basic commands?'
    },
    {
      icon: 'üêï',
      title: 'Dog Care Tips',
      question: 'What are the essential grooming needs for dogs?'
    },
    {
      icon: 'üê±',
      title: 'Cat Care Tips',
      question: 'Why is my cat not using the litter box and how can I fix it?'
    },
    {
      icon: 'üíâ',
      title: 'Vaccinations & Vet Visits',
      question: 'What vaccinations does my pet need and how often should I visit the vet?'
    }
  ];

  useEffect(() => {
    // Check if user is logged in
    if (!user) {
      navigate('/login', { state: { from: '/pawsbot', message: 'Please log in to use PawsBot' } });
      return;
    }

    // Initial welcome message
    setMessages([{
      id: Date.now(),
      type: 'bot',
      content: `Hi ${user.username}! üëã I'm PawsBot, your AI pet care assistant. Ask me anything about your furry friends!`,
      timestamp: new Date()
    }]);

    // Check if Botpress is already loaded
    if (window.botpressWebChat) {
      console.log('Botpress already loaded');
      const bpWidget = document.getElementById('bp-web-widget-container');
      if (bpWidget) {
        bpWidget.style.display = 'none';
      }
      return;
    }

    // Load Botpress with client ID
    const script = document.createElement('script');
    script.src = 'https://cdn.botpress.cloud/webchat/v2.2/inject.js';
    script.async = true;
    
    script.onload = () => {
      console.log('Botpress script loaded');
      
      // Initialize Botpress with your client ID
      window.botpressWebChat.init({
        botId: 'f540c4a7-c98e-44f5-bc83-752633f03bbf',
        hostUrl: 'https://cdn.botpress.cloud/webchat/v2.2',
        messagingUrl: 'https://messaging.botpress.cloud',
        clientId: 'f540c4a7-c98e-44f5-bc83-752633f03bbf',
        webhookId: 'your-webhook-id', // You may need to get this from Botpress
        lazySocket: true,
        themeName: 'prism',
        botName: 'PawsBot',
        avatarUrl: null,
        stylesheet: 'https://webchat-styler-css.botpress.app/prod/code/f540c4a7-c98e-44f5-bc83-752633f03bbf/v0/style.css',
        frontendVersion: 'v2.2',
        useSessionStorage: true,
        enableConversationDeletion: true,
        showPoweredBy: false,
        theme: isDarkMode ? 'dark' : 'light',
        containerWidth: '360px',
        layoutWidth: '360px',
        hideWidget: false,
        disableAnimations: false,
        closeOnEscape: true,
        showConversationsButton: false,
        enableTranscriptDownload: false,
        className: 'webchatIframe',
        containerClass: 'bp-widget-container'
      });
      
      console.log('Botpress initialized with client ID');
      
      // Wait for widget to appear
      setTimeout(() => {
        const bpWidget = document.querySelector('.bp-widget-container') || 
                        document.getElementById('bp-web-widget-container') ||
                        document.querySelector('[class*="botpress"]');
        if (bpWidget) {
          console.log('Botpress widget found and visible!');
          bpWidget.style.position = 'fixed';
          bpWidget.style.bottom = '20px';
          bpWidget.style.right = '20px';
          bpWidget.style.zIndex = '9999';
        } else {
          console.log('Botpress widget not found yet');
        }
      }, 2000);
    };

    script.onerror = () => {
      console.error('Failed to load Botpress script');
    };

    document.body.appendChild(script);

    return () => {
      // Cleanup scripts on unmount
      const scripts = document.querySelectorAll('script[src*="botpress"]');
      scripts.forEach(s => {
        if (document.body.contains(s)) {
          document.body.removeChild(s);
        }
      });
    };
  }, [user, navigate]);

  // Scroll to bottom when messages change
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Handle quick question click
  const handleQuickQuestion = (question) => {
    setInputMessage(question);
    inputRef.current?.focus();
  };

  // Send message to Botpress
  const sendMessage = async (e) => {
    e?.preventDefault();
    if (!inputMessage.trim() || isLoading) return;

    const userMessage = {
      id: Date.now(),
      type: 'user',
      content: inputMessage,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    const currentMessage = inputMessage;
    setInputMessage('');
    setIsLoading(true);

    try {
      // Send message to Botpress
      if (window.botpressWebChat && window.botpressWebChat.sendEvent) {
        console.log('Sending message to Botpress:', currentMessage);
        
        // Send the message
        window.botpressWebChat.sendEvent({
          type: 'proactive-trigger',
          channel: 'web',
          payload: { text: currentMessage }
        });

        // Listen for response from Botpress
        let responseReceived = false;
        let checkCount = 0;
        const maxChecks = 30; // Check for 15 seconds (30 * 500ms)
        
        const checkForResponse = setInterval(() => {
          checkCount++;
          
          // Look for bot messages in the Botpress iframe
          const iframe = document.querySelector('iframe[title*="Botpress"], iframe[class*="webchat"]');
          if (iframe) {
            try {
              // Try to access iframe content (may be blocked by CORS)
              const iframeDoc = iframe.contentDocument || iframe.contentWindow.document;
              const botMessages = iframeDoc.querySelectorAll('[class*="from-bot"], [class*="message-bot"]');
              
              if (botMessages.length > 0) {
                const lastMsg = botMessages[botMessages.length - 1];
                const responseText = lastMsg.textContent.trim();
                
                if (responseText && responseText !== currentMessage) {
                  responseReceived = true;
                  clearInterval(checkForResponse);
                  
                  const botMessage = {
                    id: Date.now() + 1,
                    type: 'bot',
                    content: responseText,
                    timestamp: new Date()
                  };
                  setMessages(prev => [...prev, botMessage]);
                  setIsLoading(false);
                  console.log('Bot response captured:', responseText);
                }
              }
            } catch (e) {
              // CORS blocked - can't access iframe content
              console.log('Cannot access iframe content (CORS)');
            }
          }
          
          // Timeout after max checks
          if (checkCount >= maxChecks && !responseReceived) {
            clearInterval(checkForResponse);
            const botMessage = {
              id: Date.now() + 1,
              type: 'bot',
              content: 'I\'m processing your question! For the full response, please check the chat widget in the bottom right corner. ü§ñ',
              timestamp: new Date()
            };
            setMessages(prev => [...prev, botMessage]);
            setIsLoading(false);
          }
        }, 500);
        
      } else {
        // Botpress not available
        console.warn('Botpress WebChat not available');
        const botMessage = {
          id: Date.now() + 1,
          type: 'bot',
          content: 'Please use the chat widget in the bottom right corner (ü§ñ) to chat with me!',
          timestamp: new Date()
        };
        setMessages(prev => [...prev, botMessage]);
        setIsLoading(false);
      }
    } catch (error) {
      console.error('Error in sendMessage:', error);
      const errorMessage = {
        id: Date.now() + 1,
        type: 'bot',
        content: 'Sorry, I encountered an error. Please try using the chat widget in the bottom right corner.',
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorMessage]);
      setIsLoading(false);
    }
  };

  // Handle Enter key
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div className="pawsbot-chat-container">
      {/* Header */}
      <div className="pawsbot-chat-header">
        <div className="header-content">
          <div className="header-left">
            <div className="bot-avatar">
              <span className="bot-icon">ü§ñ</span>
            </div>
            <div className="header-info">
              <h1 className="bot-title">PawsBot AI Assistant</h1>
              <p className="bot-subtitle">Your AI companion for pet care advice</p>
            </div>
          </div>
        </div>
      </div>

      {/* Main Chat Area */}
      <div className="pawsbot-chat-main">
        {messages.length === 1 ? (
          /* Welcome Screen with Quick Questions */
          <div className="welcome-screen">
            <div className="welcome-content">
              <div className="welcome-icon">
                <span>üêæ</span>
              </div>
              <h2 className="welcome-title">Welcome to PawsCare AI!</h2>
              <p className="welcome-text">
                Ask me anything about your pet's health, behavior, training, or general care.
              </p>
              
              <div className="quick-questions-grid">
                <p className="quick-questions-label">Try asking me about:</p>
                {quickQuestions.map((item, index) => (
                  <button
                    key={index}
                    className="quick-question-card"
                    onClick={() => handleQuickQuestion(item.question)}
                  >
                    <span className="quick-question-icon">{item.icon}</span>
                    <span className="quick-question-title">{item.title}</span>
                    <span className="quick-question-preview">{item.question}</span>
                  </button>
                ))}
              </div>
            </div>
          </div>
        ) : (
          /* Chat Messages */
          <div className="chat-messages">
            {messages.map((message) => (
              <div
                key={message.id}
                className={`message-wrapper ${message.type === 'user' ? 'user-message' : 'bot-message'}`}
              >
                {message.type === 'bot' && (
                  <div className="message-avatar">
                    <span className="avatar-icon">ü§ñ</span>
                  </div>
                )}
                <div className="message-content">
                  <div className="message-bubble">
                    <p className="message-text">{message.content}</p>
                  </div>
                  <span className="message-time">
                    {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                  </span>
                </div>
                {message.type === 'user' && (
                  <div className="message-avatar user-avatar">
                    <span className="avatar-icon">üë§</span>
                  </div>
                )}
              </div>
            ))}
            
            {isLoading && (
              <div className="message-wrapper bot-message">
                <div className="message-avatar">
                  <span className="avatar-icon">ü§ñ</span>
                </div>
                <div className="message-content">
                  <div className="message-bubble typing-indicator">
                    <span></span>
                    <span></span>
                    <span></span>
                  </div>
                </div>
              </div>
            )}
            
            <div ref={messagesEndRef} />
          </div>
        )}
      </div>

      {/* Input Area */}
      <div className="pawsbot-chat-input">
        <form onSubmit={sendMessage} className="input-form">
          <div className="input-wrapper">
            <textarea
              ref={inputRef}
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Ask anything about your pet..."
              className="chat-input"
              rows="1"
              disabled={isLoading}
            />
            <button
              type="submit"
              className="send-button"
              disabled={!inputMessage.trim() || isLoading}
            >
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <line x1="22" y1="2" x2="11" y2="13"></line>
                <polygon points="22 2 15 22 11 13 2 9 22 2"></polygon>
              </svg>
            </button>
          </div>
        </form>
        <p className="input-disclaimer">
          PawsBot can make mistakes. Always consult with a veterinarian for serious health concerns.
        </p>
      </div>
    </div>
  );
};

export default PawsBot;
