import React, { useState, useEffect } from 'react';
import { MapPin, Search, Phone, Navigation, ExternalLink, Cross, Store } from 'lucide-react';
import toast from 'react-hot-toast';

const PetServices = () => {
  const [location, setLocation] = useState(null);
  const [pincode, setPincode] = useState('');
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchType, setSearchType] = useState('all'); // all, hospital, store

  useEffect(() => {
    // Request user's location on mount
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude
          });
        },
        (error) => {
          console.log('Location access denied:', error);
        }
      );
    }
  }, []);

  const searchByCurrentLocation = async () => {
    if (!location) {
      toast.error('Location access denied. Please enter pincode manually.');
      return;
    }

    setLoading(true);
    try {
      await fetchNearbyServices(location.lat, location.lng);
    } catch (error) {
      toast.error('Failed to fetch nearby services');
    } finally {
      setLoading(false);
    }
  };

  const searchByPincode = async (e) => {
    e.preventDefault();

    if (!pincode || pincode.length < 5) {
      toast.error('Please enter a valid pincode');
      return;
    }

    setLoading(true);
    try {
      // Convert pincode to coordinates using geocoding
      const geocodeResponse = await fetch(
        `https://maps.googleapis.com/maps/api/geocode/json?address=${pincode}&key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}`
      );
      const geocodeData = await geocodeResponse.json();

      if (geocodeData.results && geocodeData.results.length > 0) {
        const { lat, lng } = geocodeData.results[0].geometry.location;
        await fetchNearbyServices(lat, lng);
      } else {
        toast.error('Invalid pincode. Please try again.');
      }
    } catch (error) {
      console.error('Geocoding error:', error);
      toast.error('Failed to search by pincode');
    } finally {
      setLoading(false);
    }
  };

  const fetchNearbyServices = async (lat, lng) => {
    try {
      // Note: In production, you should proxy this through your backend to hide the API key
      const types = searchType === 'all' 
        ? 'veterinary_care|pet_store' 
        : searchType === 'hospital' 
          ? 'veterinary_care' 
          : 'pet_store';

      const response = await fetch(
        `https://maps.googleapis.com/maps/api/place/nearbysearch/json?location=${lat},${lng}&radius=5000&type=${types}&keyword=pet|veterinary|animal&key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}`
      );

      const data = await response.json();

      if (data.results) {
        // Fetch details for each place to get phone numbers
        const detailedServices = await Promise.all(
          data.results.slice(0, 20).map(async (place) => {
            try {
              const detailsResponse = await fetch(
                `https://maps.googleapis.com/maps/api/place/details/json?place_id=${place.place_id}&fields=name,formatted_address,formatted_phone_number,website,rating,opening_hours,geometry&key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}`
              );
              const detailsData = await detailsResponse.json();

              return {
                id: place.place_id,
                name: place.name,
                address: place.vicinity,
                rating: place.rating,
                phone: detailsData.result?.formatted_phone_number,
                website: detailsData.result?.website,
                isOpen: detailsData.result?.opening_hours?.open_now,
                location: place.geometry.location,
                type: place.types.includes('veterinary_care') ? 'hospital' : 'store'
              };
            } catch (error) {
              console.error('Error fetching place details:', error);
              return {
                id: place.place_id,
                name: place.name,
                address: place.vicinity,
                rating: place.rating,
                location: place.geometry.location,
                type: place.types.includes('veterinary_care') ? 'hospital' : 'store'
              };
            }
          })
        );

        setServices(detailedServices);
        toast.success(`Found ${detailedServices.length} services nearby`);
      } else {
        setServices([]);
        toast.error('No services found nearby');
      }
    } catch (error) {
      console.error('Error fetching services:', error);
      toast.error('Failed to fetch nearby services');

      // Fallback to mock data for development
      setServices([
        {
          id: '1',
          name: 'City Veterinary Hospital',
          address: '123 Main Street, Your City',
          rating: 4.5,
          phone: '+1 234-567-8900',
          website: 'https://example.com',
          isOpen: true,
          type: 'hospital'
        },
        {
          id: '2',
          name: 'Pet Care Clinic',
          address: '456 Oak Avenue, Your City',
          rating: 4.8,
          phone: '+1 234-567-8901',
          isOpen: true,
          type: 'hospital'
        },
        {
          id: '3',
          name: 'Pet Supplies Plus',
          address: '789 Elm Street, Your City',
          rating: 4.3,
          phone: '+1 234-567-8902',
          website: 'https://example.com',
          isOpen: false,
          type: 'store'
        }
      ]);
    }
  };

  const openInGoogleMaps = (service) => {
    const query = encodeURIComponent(`${service.name} ${service.address}`);
    window.open(`https://www.google.com/maps/search/?api=1&query=${query}`, '_blank');
  };

  return (
    <div style={{ background: 'var(--bg-secondary)', minHeight: '100vh', padding: '2rem 1rem' }}>
      <div className="container" style={{ maxWidth: '1200px' }}>
        {/* Header */}
        <div style={{ textAlign: 'center', marginBottom: '3rem' }}>
          <h1 style={{
            fontSize: '2.5rem',
            fontWeight: 'bold',
            marginBottom: '1rem',
            background: 'linear-gradient(135deg, #8B5CF6, #10B981)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            backgroundClip: 'text'
          }}>
            üè• Pet Health & Wellness Services
          </h1>
          <p style={{ color: 'var(--text-secondary)', fontSize: '1.125rem' }}>
            Find nearby veterinary hospitals, clinics, and pet medical stores
          </p>
        </div>

        {/* Search Section */}
        <div className="card" style={{ padding: '2rem', marginBottom: '2rem' }}>
          {/* Search Type Selector */}
          <div style={{
            display: 'flex',
            gap: '1rem',
            marginBottom: '1.5rem',
            flexWrap: 'wrap',
            justifyContent: 'center'
          }}>
            {[
              { value: 'all', label: 'All Services', icon: 'üè•' },
              { value: 'hospital', label: 'Hospitals & Clinics', icon: 'üè•' },
              { value: 'store', label: 'Medical Stores', icon: 'üè™' }
            ].map(type => (
              <button
                key={type.value}
                onClick={() => setSearchType(type.value)}
                className={searchType === type.value ? 'btn btn-primary' : 'btn btn-secondary'}
                style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}
              >
                <span>{type.icon}</span>
                {type.label}
              </button>
            ))}
          </div>

          {/* Location Search */}
          <div style={{
            display: 'flex',
            gap: '1rem',
            marginBottom: '1rem',
            flexWrap: 'wrap',
            justifyContent: 'center'
          }}>
            <button
              onClick={searchByCurrentLocation}
              disabled={loading || !location}
              className="btn btn-primary"
              style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}
            >
              <Navigation size={18} />
              Use Current Location
            </button>
          </div>

          <div style={{ textAlign: 'center', margin: '1rem 0', color: 'var(--text-secondary)' }}>
            OR
          </div>

          {/* Pincode Search */}
          <form onSubmit={searchByPincode} style={{
            display: 'flex',
            gap: '1rem',
            maxWidth: '500px',
            margin: '0 auto',
            flexWrap: 'wrap'
          }}>
            <input
              type="text"
              value={pincode}
              onChange={(e) => setPincode(e.target.value)}
              placeholder="Enter pincode or zip code"
              style={{
                flex: 1,
                minWidth: '200px',
                padding: '0.75rem 1rem',
                border: '1px solid var(--border-color)',
                borderRadius: 'var(--radius-md)',
                background: 'var(--bg-secondary)',
                color: 'var(--text-primary)',
                fontSize: '1rem'
              }}
            />
            <button
              type="submit"
              disabled={loading}
              className="btn btn-secondary"
              style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}
            >
              <Search size={18} />
              Search
            </button>
          </form>
        </div>

        {/* Loading State */}
        {loading && (
          <div style={{ textAlign: 'center', padding: '3rem' }}>
            <div className="spinner" style={{ width: '3rem', height: '3rem', margin: '0 auto' }} />
            <p style={{ marginTop: '1rem', color: 'var(--text-secondary)' }}>
              Searching for nearby services...
            </p>
          </div>
        )}

        {/* Services List */}
        {!loading && services.length > 0 && (
          <div>
            <h2 style={{
              fontSize: '1.5rem',
              fontWeight: '600',
              color: 'var(--text-primary)',
              marginBottom: '1.5rem'
            }}>
              Found {services.length} Services Nearby
            </h2>

            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fill, minmax(350px, 1fr))',
              gap: '1.5rem'
            }}>
              {services.map((service) => (
                <div
                  key={service.id}
                  className="card hover-lift"
                  style={{ padding: '1.5rem' }}
                >
                  {/* Service Header */}
                  <div style={{
                    display: 'flex',
                    alignItems: 'flex-start',
                    gap: '1rem',
                    marginBottom: '1rem'
                  }}>
                    <div style={{
                      width: '50px',
                      height: '50px',
                      borderRadius: '50%',
                      background: service.type === 'hospital' 
                        ? 'linear-gradient(135deg, #EF4444, #DC2626)'
                        : 'linear-gradient(135deg, #10B981, #059669)',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      fontSize: '1.5rem',
                      flexShrink: 0
                    }}>
                      {service.type === 'hospital' ? <Cross size={24} color="white" /> : <Store size={24} color="white" />}
                    </div>

                    <div style={{ flex: 1 }}>
                      <h3 style={{
                        fontSize: '1.125rem',
                        fontWeight: '600',
                        color: 'var(--text-primary)',
                        marginBottom: '0.25rem'
                      }}>
                        {service.name}
                      </h3>

                      {service.rating && (
                        <div style={{
                          display: 'flex',
                          alignItems: 'center',
                          gap: '0.5rem',
                          fontSize: '0.875rem'
                        }}>
                          <span style={{ color: '#F59E0B' }}>‚≠ê</span>
                          <span style={{ color: 'var(--text-primary)', fontWeight: '600' }}>
                            {service.rating}
                          </span>
                          {service.isOpen !== undefined && (
                            <span style={{
                              marginLeft: '0.5rem',
                              padding: '0.125rem 0.5rem',
                              borderRadius: 'var(--radius-full)',
                              fontSize: '0.75rem',
                              fontWeight: '600',
                              background: service.isOpen ? '#D1FAE5' : '#FEE2E2',
                              color: service.isOpen ? '#059669' : '#DC2626'
                            }}>
                              {service.isOpen ? 'Open' : 'Closed'}
                            </span>
                          )}
                        </div>
                      )}
                    </div>
                  </div>

                  {/* Address */}
                  <div style={{
                    display: 'flex',
                    alignItems: 'flex-start',
                    gap: '0.5rem',
                    marginBottom: '0.75rem',
                    color: 'var(--text-secondary)',
                    fontSize: '0.875rem'
                  }}>
                    <MapPin size={16} style={{ marginTop: '2px', flexShrink: 0 }} />
                    <span>{service.address}</span>
                  </div>

                  {/* Phone */}
                  {service.phone && (
                    <div style={{
                      display: 'flex',
                      alignItems: 'center',
                      gap: '0.5rem',
                      marginBottom: '0.75rem',
                      color: 'var(--text-secondary)',
                      fontSize: '0.875rem'
                    }}>
                      <Phone size={16} />
                      <a
                        href={`tel:${service.phone}`}
                        style={{
                          color: 'var(--primary)',
                          textDecoration: 'none',
                          fontWeight: '500'
                        }}
                      >
                        {service.phone}
                      </a>
                    </div>
                  )}

                  {/* Actions */}
                  <div style={{
                    display: 'flex',
                    gap: '0.75rem',
                    marginTop: '1rem',
                    paddingTop: '1rem',
                    borderTop: '1px solid var(--border-color)'
                  }}>
                    <button
                      onClick={() => openInGoogleMaps(service)}
                      className="btn btn-primary btn-sm"
                      style={{
                        flex: 1,
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        gap: '0.5rem'
                      }}
                    >
                      <Navigation size={16} />
                      Directions
                    </button>

                    {service.website && (
                      <button
                        onClick={() => window.open(service.website, '_blank')}
                        className="btn btn-secondary btn-sm"
                        style={{
                          display: 'flex',
                          alignItems: 'center',
                          justifyContent: 'center',
                          gap: '0.5rem'
                        }}
                      >
                        <ExternalLink size={16} />
                        Website
                      </button>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Empty State */}
        {!loading && services.length === 0 && (
          <div className="card" style={{ padding: '3rem', textAlign: 'center' }}>
            <div style={{ fontSize: '4rem', marginBottom: '1rem' }}>üîç</div>
            <h3 style={{ color: 'var(--text-primary)', marginBottom: '0.5rem' }}>
              No Services Found
            </h3>
            <p style={{ color: 'var(--text-secondary)' }}>
              Try searching with your current location or enter a different pincode
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default PetServices;
