import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const PawsBot = () => {
  const [messages, setMessages] = useState([
    {
      id: 1,
      type: 'bot',
      content: "ðŸ¾ Hi there! I'm PawsBot, your AI assistant for pet care questions. How can I help you today?",
      timestamp: new Date()
    }
  ]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!inputMessage.trim() || isLoading) return;

    const userMessage = {
      id: messages.length + 1,
      type: 'user',
      content: inputMessage,
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');
    setIsLoading(true);

    try {
      // Try to connect to backend PawsBot API
      const token = localStorage.getItem('authToken');
      
      if (!token) {
        throw new Error('No authentication token found');
      }
      
      const response = await fetch('http://localhost:5000/api/chat/pawsbot', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ message: inputMessage })
      });

      if (!response.ok) {
        throw new Error(`API request failed with status: ${response.status}`);
      }

      const data = await response.json();
      
      if (data.success && data.data && data.data.response) {
        const botMessage = {
          id: messages.length + 2,
          type: 'bot',
          content: data.data.response,
          timestamp: new Date()
        };
        setMessages(prev => [...prev, botMessage]);
      } else {
        throw new Error(data.message || 'API request failed');
      }
    } catch (error) {
      console.error('Error sending message to PawsBot:', error);
      
      // Enhanced fallback AI responses based on keywords
      const botMessage = {
        id: messages.length + 2,
        type: 'bot',
        content: generateFallbackResponse(inputMessage, error),
        timestamp: new Date()
      };
      setMessages(prev => [...prev, botMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="container" style={{ padding: '2rem 1rem', maxWidth: '800px' }}>
      <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
        <h1 style={{ fontSize: '2rem', marginBottom: '0.5rem', color: 'var(--text-primary)' }}>
          ðŸ¤– PawsBot AI Assistant
        </h1>
        <p style={{ color: 'var(--text-secondary)' }}>
          Ask me anything about pet care, health, behavior, and more!
        </p>
      </div>

      {/* Chat Container */}
      <div className="card" style={{ height: '500px', display: 'flex', flexDirection: 'column' }}>
        {/* Messages Area */}
        <div style={{ 
          flex: 1, 
          overflowY: 'auto', 
          padding: '1rem',
          backgroundColor: 'var(--bg-secondary)',
          borderRadius: '0.5rem',
          marginBottom: '1rem'
        }}>
          {messages.map(message => (
            <div key={message.id} style={{ 
              marginBottom: '1rem',
              display: 'flex',
              justifyContent: message.type === 'user' ? 'flex-end' : 'flex-start'
            }}>
              <div style={{
                maxWidth: '70%',
                padding: '0.75rem 1rem',
                borderRadius: '1rem',
                backgroundColor: message.type === 'user' ? 'var(--primary)' : 'var(--card-bg)',
                color: message.type === 'user' ? 'white' : 'var(--text-primary)',
                boxShadow: 'var(--shadow-sm)',
                border: message.type === 'bot' ? '1px solid var(--border-color)' : 'none'
              }}>
                <div style={{ whiteSpace: 'pre-wrap' }}>{message.content}</div>
                <div style={{ 
                  fontSize: '0.75rem', 
                  opacity: 0.7, 
                  marginTop: '0.25rem',
                  textAlign: 'right'
                }}>
                  {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                </div>
              </div>
            </div>
          ))}
          
          {isLoading && (
            <div style={{ display: 'flex', justifyContent: 'flex-start', marginBottom: '1rem' }}>
              <div style={{
                padding: '0.75rem 1rem',
                borderRadius: '1rem',
                backgroundColor: 'var(--card-bg)',
                border: '1px solid var(--border-color)',
                boxShadow: 'var(--shadow-sm)'
              }}>
                <div className="spinner" style={{ width: '1rem', height: '1rem' }}></div>
                <span style={{ marginLeft: '0.5rem', color: 'var(--text-secondary)' }}>
                  PawsBot is thinking...
                </span>
              </div>
            </div>
          )}
        </div>

        {/* Input Area */}
        <form onSubmit={sendMessage} style={{ display: 'flex', gap: '0.5rem' }}>
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            placeholder="Ask me about your pet..."
            className="form-input"
            style={{ flex: 1 }}
            disabled={isLoading}
          />
          <button 
            type="submit" 
            className="btn btn-primary"
            disabled={isLoading || !inputMessage.trim()}
          >
            Send
          </button>
        </form>
      </div>

      {/* Quick Actions */}
      <div style={{ marginTop: '2rem', textAlign: 'center' }}>
        <h3 style={{ marginBottom: '1rem', color: 'var(--text-primary)' }}>Quick Questions</h3>
        <div style={{ display: 'flex', gap: '0.5rem', flexWrap: 'wrap', justifyContent: 'center' }}>
          {[
            "How often should I feed my cat?",
            "My dog won't stop barking",
            "What are signs of illness in pets?",
            "How to train a puppy?"
          ].map((question, index) => (
            <button
              key={index}
              onClick={() => setInputMessage(question)}
              className="btn btn-outline"
              style={{ fontSize: '0.875rem', padding: '0.5rem 1rem' }}
            >
              {question}
            </button>
          ))}
        </div>
      </div>

      <div style={{ textAlign: 'center', marginTop: '2rem' }}>
        <Link to="/" className="btn btn-ghost">
          Back to Home
        </Link>
      </div>
    </div>
  );
};

export default PawsBot;
