import React, { useEffect, useState, useRef, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';

const PawsBot = () => {
  const { user } = useAuth();
  const { isDarkMode } = useTheme();
  const navigate = useNavigate();
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);
  const botpressLoaded = useRef(false);

  // Predefined questions for quick access
  const quickQuestions = [
    {
      icon: '🍽️',
      title: 'Pet Nutrition & Feeding',
      question: 'What is the best diet for my dog? How often should I feed them?'
    },
    {
      icon: '🏥',
      title: 'Health & Wellness',
      question: 'What are the common signs of illness in pets that I should watch for?'
    },
    {
      icon: '🎓',
      title: 'Training & Behavior',
      question: 'How can I train my puppy to stop biting and follow basic commands?'
    },
    {
      icon: '🐕',
      title: 'Dog Care Tips',
      question: 'What are the essential grooming needs for dogs?'
    },
    {
      icon: '🐱',
      title: 'Cat Care Tips',
      question: 'Why is my cat not using the litter box and how can I fix it?'
    },
    {
      icon: '💉',
      title: 'Vaccinations & Vet Visits',
      question: 'What vaccinations does my pet need and how often should I visit the vet?'
    }
  ];

  // Apply theme customization to Botpress widget
  const applyBotpressTheme = useCallback(() => {
    // Inject custom CSS to style Botpress widget based on theme
    const styleId = 'botpress-custom-theme';
    let styleElement = document.getElementById(styleId);

    if (!styleElement) {
      styleElement = document.createElement('style');
      styleElement.id = styleId;
      document.head.appendChild(styleElement);
    }

    const customStyles = `
      /* Botpress Widget Theme Customization */
      #bp-web-widget-container {
        z-index: 1000;
      }

      #bp-web-widget {
        ${isDarkMode ? `
          --bp-color-primary: #3B82F6;
          --bp-color-background: #1E293B;
          --bp-color-text: #F1F5F9;
          --bp-color-text-secondary: #94A3B8;
          --bp-color-border: #334155;
        ` : `
          --bp-color-primary: #2563EB;
          --bp-color-background: #FFFFFF;
          --bp-color-text: #1E293B;
          --bp-color-text-secondary: #64748B;
          --bp-color-border: #E2E8F0;
        `}
      }

      /* Mobile responsiveness */
      @media (max-width: 768px) {
        #bp-web-widget-container {
          width: 100% !important;
          height: 100% !important;
          max-width: 100% !important;
          max-height: 100% !important;
        }
      }

      @media (max-width: 480px) {
        .pawsbot-container {
          padding: 1rem 0.5rem !important;
        }

        .pawsbot-header h1 {
          font-size: 1.5rem !important;
        }

        .pawsbot-info-card {
          padding: 1rem !important;
        }
      }
    `;

    styleElement.textContent = customStyles;
  }, [isDarkMode]);

  useEffect(() => {
    // Check if user is logged in
    if (!user) {
      navigate('/login', { state: { from: '/pawsbot', message: 'Please log in to use PawsBot' } });
      return;
    }

    // Load chat history from localStorage
    const savedHistory = localStorage.getItem(`pawsbot_history_${user.id}`);
    if (savedHistory) {
      try {
        setChatHistory(JSON.parse(savedHistory));
      } catch (error) {
        console.error('Error loading chat history:', error);
      }
    }

    // Load Botpress scripts only once
    if (!botpressLoaded.current) {
      const script1 = document.createElement('script');
      script1.src = 'https://cdn.botpress.cloud/webchat/v3.3/inject.js';
      script1.async = true;

      const script2 = document.createElement('script');
      script2.src = 'https://files.bpcontent.cloud/2025/10/01/08/20251001082958-80TU2NR9.js';
      script2.defer = true;

      script1.onload = () => {
        console.log('Botpress inject script loaded');
      };

      script2.onload = () => {
        console.log('Botpress config script loaded');
        setIsLoaded(true);
        botpressLoaded.current = true;

        // Apply theme customization after Botpress loads
        setTimeout(() => {
          applyBotpressTheme();
        }, 1000);
      };

      document.body.appendChild(script1);
      document.body.appendChild(script2);

      return () => {
        // Cleanup scripts on unmount
        if (document.body.contains(script1)) {
          document.body.removeChild(script1);
        }
        if (document.body.contains(script2)) {
          document.body.removeChild(script2);
        }
      };
    } else {
      setIsLoaded(true);
      applyBotpressTheme();
    }
  }, [user, navigate, applyBotpressTheme]);

  // Apply theme customization to Botpress widget
  useEffect(() => {
    if (isLoaded) {
      applyBotpressTheme();
    }
  }, [isDarkMode, isLoaded, applyBotpressTheme]);

  // Track conversation in history
  useEffect(() => {
    if (user && window.botpressWebChat) {
      // Listen for messages (if Botpress provides such API)
      // This is a placeholder - actual implementation depends on Botpress API
      const saveToHistory = (message) => {
        const newHistory = [...chatHistory, {
          message,
          timestamp: new Date().toISOString()
        }].slice(-10); // Keep last 10 conversations

        setChatHistory(newHistory);
        localStorage.setItem(`pawsbot_history_${user.id}`, JSON.stringify(newHistory));
      };
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [user]);

  const clearHistory = () => {
    if (user) {
      setChatHistory([]);
      localStorage.removeItem(`pawsbot_history_${user.id}`);
    }
  };

  return (
    <div className="pawsbot-container" style={{ 
      padding: '2rem 1rem', 
      maxWidth: '1200px', 
      margin: '0 auto',
      minHeight: 'calc(100vh - 80px)'
    }}>
      {/* Header */}
      <div className="pawsbot-header" style={{ 
        textAlign: 'center', 
        marginBottom: '2rem' 
      }}>
        <h1 style={{ 
          fontSize: '2.5rem', 
          marginBottom: '0.5rem', 
          color: 'var(--text-primary)',
          fontWeight: '700'
        }}>
          🐾 PawsBot
        </h1>
        <p style={{ 
          color: 'var(--text-secondary)',
          fontSize: '1.1rem'
        }}>
          Your AI-powered pet care assistant
        </p>
        {user && (
          <p style={{ 
            color: 'var(--text-secondary)',
            fontSize: '0.9rem',
            marginTop: '0.5rem'
          }}>
            Welcome back, <strong>{user.username}</strong>! 👋
          </p>
        )}
      </div>

      {/* Main Content */}
      <div style={{ 
        display: 'grid', 
        gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
        gap: '2rem',
        marginBottom: '2rem'
      }}>
        {/* Info Card */}
        <div className="pawsbot-info-card card" style={{ 
          padding: '1.5rem',
          backgroundColor: 'var(--card-bg)',
          borderRadius: '1rem',
          boxShadow: 'var(--shadow-md)'
        }}>
          <h2 style={{ 
            fontSize: '1.5rem', 
            marginBottom: '1rem', 
            color: 'var(--text-primary)' 
          }}>
            💬 How to Use PawsBot
          </h2>
          <ul style={{ 
            listStyle: 'none', 
            padding: 0,
            color: 'var(--text-secondary)',
            lineHeight: '1.8'
          }}>
            <li style={{ marginBottom: '0.75rem' }}>
              ✅ Click the chat icon in the bottom right corner
            </li>
            <li style={{ marginBottom: '0.75rem' }}>
              ✅ Ask questions about pet care, health, training, and more
            </li>
            <li style={{ marginBottom: '0.75rem' }}>
              ✅ Get instant AI-powered responses
            </li>
            <li style={{ marginBottom: '0.75rem' }}>
              ✅ Your chat history is saved automatically
            </li>
          </ul>
        </div>

        {/* Recent Questions */}
        {chatHistory.length > 0 && (
          <div className="card" style={{ 
            padding: '1.5rem',
            backgroundColor: 'var(--card-bg)',
            borderRadius: '1rem',
            boxShadow: 'var(--shadow-md)'
          }}>
            <div style={{ 
              display: 'flex', 
              justifyContent: 'space-between', 
              alignItems: 'center',
              marginBottom: '1rem'
            }}>
              <h2 style={{ 
                fontSize: '1.5rem', 
                color: 'var(--text-primary)',
                margin: 0
              }}>
                📝 Recent Conversations
              </h2>
              <button 
                onClick={clearHistory}
                className="btn btn-ghost"
                style={{ 
                  fontSize: '0.875rem',
                  padding: '0.25rem 0.75rem'
                }}
              >
                Clear
              </button>
            </div>
            <div style={{ 
              maxHeight: '300px', 
              overflowY: 'auto',
              color: 'var(--text-secondary)'
            }}>
              {chatHistory.map((item, index) => (
                <div 
                  key={index} 
                  style={{ 
                    padding: '0.75rem',
                    marginBottom: '0.5rem',
                    backgroundColor: 'var(--bg-secondary)',
                    borderRadius: '0.5rem',
                    fontSize: '0.9rem'
                  }}
                >
                  <div style={{ fontWeight: '500', marginBottom: '0.25rem' }}>
                    {item.message}
                  </div>
                  <div style={{ 
                    fontSize: '0.75rem', 
                    opacity: 0.7 
                  }}>
                    {new Date(item.timestamp).toLocaleString()}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

      {/* Quick Tips */}
      <div className="card" style={{ 
        padding: '1.5rem',
        backgroundColor: 'var(--card-bg)',
        borderRadius: '1rem',
        boxShadow: 'var(--shadow-md)',
        marginBottom: '2rem'
      }}>
        <h2 style={{ 
          fontSize: '1.5rem', 
          marginBottom: '1rem', 
          color: 'var(--text-primary)',
          textAlign: 'center'
        }}>
          🎯 Try Asking About
        </h2>
        <div style={{ 
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
          gap: '1rem'
        }}>
          {[
            { icon: '🍽️', text: 'Pet Nutrition & Feeding' },
            { icon: '🏥', text: 'Health & Wellness' },
            { icon: '🎓', text: 'Training & Behavior' },
            { icon: '🐕', text: 'Dog Care Tips' },
            { icon: '🐱', text: 'Cat Care Tips' },
            { icon: '💉', text: 'Vaccinations & Vet Visits' }
          ].map((topic, index) => (
            <div 
              key={index}
              style={{
                padding: '1rem',
                backgroundColor: 'var(--bg-secondary)',
                borderRadius: '0.75rem',
                textAlign: 'center',
                color: 'var(--text-primary)',
                fontSize: '0.95rem',
                fontWeight: '500',
                transition: 'transform 0.2s ease',
                cursor: 'default'
              }}
              onMouseEnter={(e) => e.currentTarget.style.transform = 'translateY(-2px)'}
              onMouseLeave={(e) => e.currentTarget.style.transform = 'translateY(0)'}
            >
              <div style={{ fontSize: '2rem', marginBottom: '0.5rem' }}>
                {topic.icon}
              </div>
              {topic.text}
            </div>
          ))}
        </div>
      </div>

      {/* Loading Indicator */}
      {!isLoaded && (
        <div style={{ 
          textAlign: 'center', 
          padding: '2rem',
          color: 'var(--text-secondary)'
        }}>
          <div className="spinner" style={{ 
            width: '3rem', 
            height: '3rem',
            margin: '0 auto 1rem'
          }}></div>
          <p>Loading PawsBot...</p>
        </div>
      )}

      {/* Back Button */}
      <div style={{ textAlign: 'center', marginTop: '2rem' }}>
        <Link to="/" className="btn btn-ghost">
          ← Back to Home
        </Link>
      </div>
    </div>
  );
};
export default PawsBot;
